mapper.select:
SELECT B.* FROM(
	SELECT rownum() AS rn, A.* FROM(
		SELECT write_code, nickname, title, TEXT, filename, DATE, hit, address 
		FROM member m, writer w where m.id = w.id 

		ORDER BY write_code DESC
	)A
)B
WHERE B.rn >= #{start} AND B.rn <= #{end}

mapper.update:
update writer set pet_code = #{pet_code}, title = #{title}, text = #{text}, 
				  filename = #{filename}
where write_code = #{write_code}
